SELECT COUNT(*) FROM comments as c, posts as p, votes as v, users as u WHERE u.Id = c.UserId AND u.Id = p.OwnerUserId AND u.Id = v.UserId AND c.Score = 6 AND c.CreationDate <= '2013-07-29 17:18:28'::timestamp AND p.CreationDate >= '2010-12-31 20:00:23'::timestamp AND v.BountyAmount <= 134 AND v.CreationDate <= '2012-12-12 04:10:07'::timestamp AND u.UpVotes >= 480 AND u.UpVotes <= 10730 AND u.CreationDate >= '2011-10-08 15:24:58'::timestamp;