SELECT COUNT(*) FROM votes as v, posts as p, badges as b, users as u WHERE u.Id = v.UserId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Score >= 98 AND p.Score <= 145 AND p.CommentCount = 23 AND p.CreationDate >= '2013-06-25 10:02:29'::timestamp AND p.CreationDate <= '2014-09-05 17:37:00'::timestamp;