SELECT COUNT(*) FROM votes as v, posts as p, badges as b, users as u WHERE u.Id = v.UserId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Score >= 25 AND p.Score <= 96 AND p.CommentCount = 8 AND p.CreationDate >= '2013-08-23 03:57:24'::timestamp AND p.CreationDate <= '2014-07-28 03:13:23'::timestamp;