SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 10 AND c.CreationDate >= '2011-01-13 02:52:37'::timestamp AND c.CreationDate <= '2013-01-29 07:29:28'::timestamp AND ph.PostHistoryTypeId = 2 AND ph.CreationDate >= '2012-05-13 10:50:22'::timestamp AND ph.CreationDate <= '2013-04-05 23:42:59'::timestamp AND b.Date <= '2011-04-20 18:48:58'::timestamp;