SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 2 AND c.CreationDate >= '2014-09-03 14:22:41'::timestamp AND c.CreationDate <= '2014-09-10 13:44:40'::timestamp AND ph.PostHistoryTypeId = 8 AND ph.CreationDate >= '2011-09-12 13:37:24'::timestamp AND ph.CreationDate <= '2014-08-22 17:02:16'::timestamp AND b.Date <= '2014-01-23 10:06:58'::timestamp;