SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 1 AND c.CreationDate >= '2014-06-26 22:45:07'::timestamp AND ph.PostHistoryTypeId = 4 AND ph.CreationDate >= '2011-08-01 12:51:09'::timestamp AND u.Reputation >= 778 AND u.Reputation <= 60159 AND u.DownVotes <= 859 AND u.CreationDate >= '2012-04-03 05:22:03'::timestamp AND u.CreationDate <= '2013-06-10 02:07:02'::timestamp;