SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 4 AND c.CreationDate >= '2011-12-26 16:59:52'::timestamp AND ph.PostHistoryTypeId = 16 AND ph.CreationDate >= '2009-09-12 20:09:27'::timestamp AND u.Reputation >= 37331 AND u.Reputation <= 58904 AND u.DownVotes <= 1475 AND u.CreationDate >= '2010-07-25 21:52:11'::timestamp AND u.CreationDate <= '2014-01-16 20:57:53'::timestamp;