SELECT COUNT(*) FROM comments as c, posts as p, votes as v, badges as b, users as u WHERE u.Id =c.UserId AND c.UserId = p.OwnerUserId AND p.OwnerUserId = v.UserId AND v.UserId = b.UserId AND c.Score=2 AND p.Score>=21 AND p.Score<=161 AND p.ViewCount<=57942 AND p.CommentCount=3 AND p.FavoriteCount>=25 AND u.Reputation>=8531 AND u.Reputation<=51046 AND u.Views>=4800 AND u.Views<=15133;