SELECT COUNT(*) FROM comments as c, posts as p, votes as v, badges as b, users as u WHERE u.Id =c.UserId AND c.UserId = p.OwnerUserId AND p.OwnerUserId = v.UserId AND v.UserId = b.UserId AND c.Score=4 AND p.Score>=15 AND p.Score<=134 AND p.ViewCount<=122851 AND p.CommentCount=0 AND p.FavoriteCount>=11 AND u.Reputation>=12315 AND u.Reputation<=83064 AND u.Views>=2589 AND u.Views<=18692;