SELECT COUNT(*) FROM comments as c, posts as p, votes as v, badges as b, users as u WHERE u.Id =c.UserId AND c.UserId = p.OwnerUserId AND p.OwnerUserId = v.UserId AND v.UserId = b.UserId AND c.Score=0 AND p.Score>=48 AND p.Score<=60 AND p.ViewCount<=135250 AND p.CommentCount=0 AND p.FavoriteCount>=8 AND u.Reputation>=26022 AND u.Reputation<=55900 AND u.Views>=4656 AND u.Views<=18760;