SELECT COUNT(*) FROM posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND u.Id = ph.UserId AND u.Id = v.UserId AND p.CommentCount>=0 AND p.CommentCount<=22 AND ph.PostHistoryTypeId=4 AND ph.CreationDate<='2011-09-11 16:24:14'::timestamp AND v.CreationDate>='2009-11-11 06:39:58'::timestamp AND b.Date<='2013-07-10 17:30:42'::timestamp AND u.Views>=3218 AND u.DownVotes>=250 AND u.CreationDate>='2010-12-17 22:47:35'::timestamp AND u.CreationDate<='2014-03-14 22:03:42'::timestamp;