SELECT COUNT(*) FROM posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND u.Id = ph.UserId AND u.Id = v.UserId AND p.CommentCount>=3 AND p.CommentCount<=22 AND ph.PostHistoryTypeId=4 AND ph.CreationDate<='2011-07-23 11:26:16'::timestamp AND v.CreationDate>='2010-09-22 19:42:20'::timestamp AND b.Date<='2012-05-28 22:28:04'::timestamp AND u.Views>=12909 AND u.DownVotes>=46 AND u.CreationDate>='2010-07-31 17:57:30'::timestamp AND u.CreationDate<='2010-09-11 07:38:16'::timestamp;