SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND u.Views<=5128 AND u.CreationDate>='2011-12-04 07:56:55'::timestamp AND u.CreationDate<='2014-07-01 01:20:39'::timestamp;