SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND u.Views<=5413 AND u.CreationDate>='2014-07-02 22:52:38'::timestamp AND u.CreationDate<='2014-08-26 23:00:00'::timestamp;