SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND c.CreationDate>='2010-06-11 06:34:55'::timestamp AND c.CreationDate<='2014-08-29 02:46:16'::timestamp AND p.Score>=-4 AND p.Score<=22 AND p.FavoriteCount>=2 AND pl.LinkTypeId=1 AND pl.CreationDate>='2013-07-08 15:58:32'::timestamp AND v.VoteTypeId=8 AND v.CreationDate<='2014-01-06 20:49:55'::timestamp AND u.Reputation>=4206 AND u.CreationDate>='2011-01-28 06:39:15'::timestamp AND u.CreationDate<='2012-09-11 07:56:01'::timestamp;