SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND c.CreationDate>='2011-07-18 00:49:48'::timestamp AND c.CreationDate<='2012-04-18 03:35:38'::timestamp AND p.Score>=-7 AND p.Score<=19 AND p.FavoriteCount>=17 AND pl.LinkTypeId=1 AND pl.CreationDate>='2011-02-23 18:11:22'::timestamp AND v.VoteTypeId=5 AND v.CreationDate<='2011-07-03 20:30:10'::timestamp AND u.Reputation>=578 AND u.CreationDate>='2010-09-29 13:17:41'::timestamp AND u.CreationDate<='2011-09-08 19:11:47'::timestamp;