SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND c.Score=4 AND p.ViewCount<=72340 AND p.CommentCount<=3 AND p.FavoriteCount>=18 AND p.FavoriteCount<=122 AND p.CreationDate>='2010-05-27 21:31:19'::timestamp AND u.UpVotes>=7716;