SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND c.Score=2 AND p.ViewCount<=12384 AND p.CommentCount<=11 AND p.FavoriteCount>=41 AND p.FavoriteCount<=217 AND p.CreationDate>='2010-10-19 03:11:17'::timestamp AND u.UpVotes>=1071;