SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND c.Score=0 AND p.ViewCount<=10593 AND p.CommentCount<=43 AND p.FavoriteCount>=15 AND p.FavoriteCount<=181 AND p.CreationDate>='2012-01-02 03:37:44'::timestamp AND u.UpVotes>=1612;