SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, users as u WHERE p.Id = pl.PostId AND p.Id = ph.PostId AND p.Id = c.PostId AND u.Id = c.UserId AND u.Id = v.UserId AND c.CreationDate<='2011-11-12 23:02:32'::timestamp AND p.PostTypeId=1 AND p.Score=47 AND p.FavoriteCount<=227 AND pl.CreationDate>='2010-12-19 21:21:49'::timestamp AND pl.CreationDate<='2014-05-19 14:07:41'::timestamp AND ph.CreationDate>='2012-11-22 18:33:12'::timestamp AND ph.CreationDate<='2014-03-17 11:06:24'::timestamp AND v.CreationDate>='2010-11-21 19:54:03'::timestamp AND u.Reputation>=37931 AND u.DownVotes>=388;