SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=21 AND p.Score<=131 AND p.ViewCount<=122493 AND pl.LinkTypeId=1 AND v.CreationDate<='2014-07-11 07:10:22'::timestamp;