SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.CreationDate>='2010-04-17 05:45:59'::timestamp AND c.CreationDate<='2011-12-22 10:47:00'::timestamp AND p.Score<=176 AND p.ViewCount<=141481 AND p.AnswerCount>=28 AND p.AnswerCount<=130 AND p.CommentCount<=32 AND p.FavoriteCount>=41 AND v.VoteTypeId=3 AND v.CreationDate>='2010-02-26 06:17:17'::timestamp AND b.Date>='2012-01-12 12:44:36'::timestamp;