SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.CreationDate>='2011-07-29 05:24:13'::timestamp AND c.CreationDate<='2013-05-31 08:51:08'::timestamp AND p.Score<=52 AND p.ViewCount<=45003 AND p.AnswerCount>=26 AND p.AnswerCount<=75 AND p.CommentCount<=7 AND p.FavoriteCount>=42 AND v.VoteTypeId=15 AND v.CreationDate>='2011-03-01 10:52:50'::timestamp AND b.Date>='2010-11-01 02:36:52'::timestamp;