SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=6 AND p.Score<=8 AND ph.PostHistoryTypeId=5 AND b.Date<='2010-08-27 09:17:43'::timestamp;