SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=2 AND p.Score<=162 AND ph.PostHistoryTypeId=36 AND b.Date<='2013-03-20 18:52:57'::timestamp;