SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.Score=15 AND p.Score<=187 AND ph.PostHistoryTypeId=2 AND b.Date<='2011-06-28 07:23:58'::timestamp;