SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b WHERE p.Id = c.PostId AND p.Id = pl.RelatedPostId AND p.Id = ph.PostId AND p.Id = v.PostId AND b.UserId = c.UserId AND c.CreationDate>='2012-04-08 21:50:41'::timestamp AND p.Score>=57 AND p.ViewCount>=103872 AND p.CommentCount<=13 AND ph.CreationDate>='2012-02-19 21:39:41'::timestamp AND ph.CreationDate<='2014-06-26 19:00:38'::timestamp AND v.VoteTypeId=2;