SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND c.Score=20 AND c.CreationDate<='2014-05-16 08:56:45'::timestamp AND p.Score>=77 AND p.ViewCount>=17893 AND p.ViewCount<=118097 AND p.AnswerCount<=125 AND p.CommentCount>=2 AND p.CommentCount<=24 AND p.CreationDate>='2009-11-23 01:43:00'::timestamp AND u.Reputation<=3380 AND u.DownVotes<=1505;