SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND p.PostTypeId=1 AND p.ViewCount<=52694 AND p.CommentCount>=0 AND p.CommentCount<=26 AND ph.PostHistoryTypeId=10 AND v.VoteTypeId=3 AND u.Views<=7865 AND u.DownVotes>=101 AND u.DownVotes<=1521 AND u.UpVotes<=4626;