SELECT COUNT(*) FROM comments as c, posts as p, postHistory as ph, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Id = v.PostId AND c.Score=8 AND p.Score<=100 AND p.AnswerCount<=28 AND p.FavoriteCount>=15 AND v.CreationDate>='2010-06-04 05:29:46'::timestamp AND b.Date<='2011-09-17 05:32:07'::timestamp AND u.Reputation<=7585;