SELECT COUNT(*) FROM comments as c, posts as p, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND c.Score=0 AND c.CreationDate<='2013-05-08 16:28:19'::timestamp AND p.Score>=-13 AND p.Score<=110 AND p.CommentCount<=1 AND p.CreationDate<='2010-08-21 10:05:13'::timestamp AND v.CreationDate<='2010-10-30 10:20:50'::timestamp AND u.DownVotes>=323;