SELECT COUNT(*) FROM comments as c, posts as p, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND c.Score=0 AND c.CreationDate<='2012-11-20 08:08:15'::timestamp AND p.Score>=51 AND p.Score<=120 AND p.CommentCount<=14 AND p.CreationDate<='2012-03-13 10:51:21'::timestamp AND v.CreationDate<='2013-08-19 05:48:54'::timestamp AND u.DownVotes>=518;