SELECT COUNT(*) FROM comments as c, posts as p, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND c.Score=7 AND c.CreationDate<='2013-12-19 07:49:03'::timestamp AND p.Score>=7 AND p.Score<=80 AND p.CommentCount<=19 AND p.CreationDate<='2011-12-05 13:15:00'::timestamp AND v.CreationDate<='2013-08-01 15:40:32'::timestamp AND u.DownVotes>=413;