SELECT COUNT(*) FROM postHistory as ph, posts as p, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = ph.PostId AND p.Id = v.PostId AND ph.CreationDate<='2012-05-04 15:02:59'::timestamp AND p.PostTypeId=1 AND p.AnswerCount>=4 AND p.AnswerCount<=21 AND v.CreationDate>='2011-10-06 05:22:26'::timestamp AND v.CreationDate<='2012-07-22 07:59:23'::timestamp AND u.DownVotes=6 AND u.CreationDate<='2012-12-16 11:07:45'::timestamp;