SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v WHERE p.Id = pl.PostId AND p.Id = v.PostId AND p.Id = ph.PostId AND p.Id = c.PostId AND c.Score=2 AND p.FavoriteCount>=97 AND p.CreationDate>='2009-04-22 19:06:27'::timestamp AND p.CreationDate<='2012-08-10 03:52:47'::timestamp AND pl.LinkTypeId=1 AND pl.CreationDate>='2012-07-02 01:42:38'::timestamp AND v.VoteTypeId=3;