SELECT COUNT(*) FROM comments as c, postLinks as pl, posts as p, users as u, badges as b WHERE u.Id = b.UserId AND u.Id = p.OwnerUserId AND p.Id = c.PostId AND p.Id = pl.RelatedPostId AND c.CreationDate<='2012-08-04 01:42:26'::timestamp AND pl.LinkTypeId=3 AND pl.CreationDate>='2011-07-26 15:21:57'::timestamp AND pl.CreationDate<='2013-07-03 13:51:58'::timestamp AND p.Score>=22 AND p.Score<=156 AND p.AnswerCount>=18 AND p.AnswerCount<=27 AND p.CommentCount>=4 AND p.CommentCount<=32 AND p.FavoriteCount<=159 AND p.CreationDate>='2009-12-20 05:58:43'::timestamp AND p.CreationDate<='2014-04-26 19:40:17'::timestamp;