SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=-18 AND p.Score<=177 AND pl.CreationDate<='2012-05-30 08:39:36'::timestamp AND v.CreationDate>='2010-02-08 22:11:36'::timestamp AND b.Date>='2011-11-05 06:00:37'::timestamp AND b.Date<='2013-04-12 12:45:21'::timestamp AND u.DownVotes>=261;