SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=2 AND p.Score<=151 AND pl.CreationDate<='2013-10-07 16:58:50'::timestamp AND v.CreationDate>='2011-05-22 01:28:04'::timestamp AND b.Date>='2012-08-25 18:55:54'::timestamp AND b.Date<='2014-05-20 04:07:08'::timestamp AND u.DownVotes>=603;