SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=2 AND p.Score<=78 AND pl.CreationDate<='2014-07-20 08:29:48'::timestamp AND v.CreationDate>='2009-08-25 16:22:21'::timestamp AND b.Date>='2012-09-20 01:27:00'::timestamp AND b.Date<='2014-05-11 12:36:50'::timestamp AND u.DownVotes>=215;