SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=-16 AND p.Score<=181 AND pl.CreationDate<='2012-02-22 12:16:10'::timestamp AND v.CreationDate>='2012-11-23 00:47:26'::timestamp AND b.Date>='2013-02-22 00:51:48'::timestamp AND b.Date<='2014-04-03 05:55:44'::timestamp AND u.DownVotes>=474;