SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, votes as v, badges as b, users as u WHERE u.Id = p.OwnerUserId AND p.Id = pl.RelatedPostId AND p.Id = v.PostId AND p.Id = c.PostId AND u.Id = b.UserId AND p.Score>=13 AND p.Score<=149 AND pl.CreationDate<='2011-09-21 14:39:57'::timestamp AND v.CreationDate>='2014-05-11 15:13:40'::timestamp AND b.Date>='2010-10-26 22:03:10'::timestamp AND b.Date<='2014-03-17 07:58:28'::timestamp AND u.DownVotes>=510;