SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Score<=52 AND p.CommentCount>=8 AND p.CreationDate>='2012-10-19 14:33:52'::timestamp AND p.CreationDate<='2014-07-23 05:37:31'::timestamp AND pl.LinkTypeId=3;