SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Score<=103 AND p.CommentCount>=22 AND p.CreationDate>='2011-11-14 08:18:07'::timestamp AND p.CreationDate<='2014-03-18 19:05:03'::timestamp AND pl.LinkTypeId=3;