SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND p.Score<=121 AND p.CommentCount>=3 AND p.CreationDate>='2011-04-13 18:00:09'::timestamp AND p.CreationDate<='2011-11-26 06:04:05'::timestamp AND pl.LinkTypeId=1;