SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, badges as b, users as u WHERE p.Id = pl.RelatedPostId AND b.UserId = u.Id AND c.UserId = u.Id AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = ph.PostId AND c.CreationDate>='2012-05-06 16:24:17'::timestamp AND c.CreationDate<='2013-10-24 15:24:45'::timestamp AND p.AnswerCount<=37 AND p.FavoriteCount>=11 AND pl.LinkTypeId=1 AND v.VoteTypeId=15 AND v.CreationDate<='2013-02-09 20:41:38'::timestamp AND b.Date<='2014-03-20 05:58:31'::timestamp;