SELECT COUNT(*) FROM comments as c, posts as p, postLinks as pl, postHistory as ph, votes as v, users as u WHERE u.Id = p.OwnerUserId AND p.Id = v.PostId AND p.Id = c.PostId AND p.Id = pl.PostId AND p.Id = ph.PostId AND c.CreationDate>='2012-06-02 17:37:01'::timestamp AND p.Score>=-13 AND p.CommentCount<=39 AND p.CreationDate>='2009-10-02 00:01:34'::timestamp AND p.CreationDate<='2013-06-07 14:45:54'::timestamp AND pl.CreationDate<='2013-10-23 02:01:19'::timestamp AND ph.CreationDate>='2010-11-13 14:59:18'::timestamp AND u.DownVotes>=498 AND u.UpVotes>=2387;