SELECT COUNT(*) FROM votes as v, posts as p, badges as b, users as u WHERE u.Id = v.UserId AND u.Id = p.OwnerUserId AND u.Id = b.UserId AND p.Score >= 9 AND p.Score <= 147 AND p.CommentCount = 25 AND p.CreationDate >= '2011-01-01 05:48:15'::timestamp AND p.CreationDate <= '2014-06-30 13:01:52'::timestamp;