SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 18 AND c.CreationDate >= '2009-04-23 07:14:48'::timestamp AND c.CreationDate <= '2011-09-05 18:06:40'::timestamp AND ph.PostHistoryTypeId = 1 AND ph.CreationDate >= '2012-03-25 03:12:39'::timestamp AND ph.CreationDate <= '2012-12-24 19:24:20'::timestamp AND b.Date <= '2012-01-08 08:42:47'::timestamp;