SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 8 AND c.CreationDate >= '2012-06-11 08:00:08'::timestamp AND c.CreationDate <= '2012-09-22 22:00:03'::timestamp AND ph.PostHistoryTypeId = 10 AND ph.CreationDate >= '2012-09-23 01:20:52'::timestamp AND ph.CreationDate <= '2013-05-16 08:12:26'::timestamp AND b.Date <= '2013-04-09 00:22:21'::timestamp;