SELECT COUNT(*) FROM comments as c, postHistory as ph, badges as b, users as u WHERE u.Id = c.UserId AND u.Id = ph.UserId AND u.Id = b.UserId AND c.Score = 10 AND c.CreationDate >= '2011-03-16 11:23:38'::timestamp AND ph.PostHistoryTypeId = 16 AND ph.CreationDate >= '2010-07-28 09:35:17'::timestamp AND u.Reputation >= 14747 AND u.Reputation <= 51990 AND u.DownVotes <= 1364 AND u.CreationDate >= '2010-10-26 00:19:19'::timestamp AND u.CreationDate <= '2014-07-16 19:32:15'::timestamp;